import {useDeployContract, useReadContract, useTransactionReceipt, useWriteContract} from "wagmi";
import {hederaTestnet} from "wagmi/chains";

// You can find out in the documentation how to this files
import abi from "../../contract/contract_Greeter_sol_Greeter.json"
// contract_Greeter_sol_Greeter.bin
const bin = ""

export function useDeploy() {
  const {data: deployTxHash, deployContract, status: deployStatus} = useDeployContract()
  const {data: deployReceipt, status: receiptStatus} = useTransactionReceipt({
    hash: deployTxHash,
    query: {
      enabled: Boolean(deployTxHash),
    }
  })

  return {
    deployContract: () => {
      deployContract({
        bytecode: `0x${bin}`,
        abi,
        args: ["Hello from react"],
      })
    },
    deployTxHash,
    deployReceipt,
    contractAddress: deployReceipt?.contractAddress ?? undefined,
    isDeployed: deployStatus === "success" && receiptStatus === "success",
  }
}

type ContractAddress = `0x${string}` | undefined;

export function useWriteGreet(address: ContractAddress, refetch: () => void) {
  const {writeContractAsync, status, data} = useWriteContract()

  return {
    setGreeting: async (greet: string) => {
      if (!address) {
        return
      }
      await writeContractAsync({
        abi,
        address,
        functionName: "setGreeting",
        args: [greet],
      })
      refetch()
    },
    status,
    data
  }
}

export function useReadGreet(address: ContractAddress) {
  return useReadContract({
    query: {
      enabled: Boolean(address),
    },
    abi,
    address,
    functionName: "greet",
    args: [],
    chainId: hederaTestnet.id,
  })
}
